create procedure application_settings_insertupdate(	@id int = 0,	@Name nvarchar(500),    @Value nchar(1000) = null,    @Group nvarchar(500) = null,    @Description nvarchar(2000) = null)asif exists(select 1 from application_settings where id  = @id)	begin 		update application_settings 		set 			[Name] = @Name,			[Value] = @Value,			[Description] = @Description,			[Group] = @Group		where Id = @id	endelse	begin  		insert into [dbo].[application_settings]			   (			    [Name]			   ,[Value]			   ,[Group]			   ,[Description]			   )		 values			   (				@Name			   ,@Value			   ,@Group			   ,@Description			   )	end